import React, { useState } from 'react';
import styled from 'styled-components';
import { MapPin, ShieldAlert, Route, Clock, Settings, Bell, Navigation, AlertTriangle } from 'lucide-react';
import { planCommute, riskLegend } from '../../services/commuteService';
import CommuteMap from './CommuteMap';

const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 16px;
  padding: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const Title = styled.h2`
  color: #333;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: 1.1fr 1fr;
  gap: 20px;
  @media (max-width: 960px) {
    grid-template-columns: 1fr;
  }
`;

const FieldRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 12px;
  margin-bottom: 12px;
  @media (max-width: 640px) {
    grid-template-columns: 1fr;
  }
`;

const Label = styled.label`
  font-size: 0.85rem;
  color: #555;
  margin-bottom: 6px;
  display: block;
`;

const Input = styled.input`
  width: 100%;
  border: 2px solid #e1e5e9;
  border-radius: 10px;
  padding: 10px 12px;
  font-size: 0.95rem;
  &:focus { outline: none; border-color: #667eea; box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1); }
`;

const Range = styled.input`
  width: 100%;
`;

const Button = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 10px 16px;
  border-radius: 10px;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-weight: 600;
`;

const MapPlaceholder = styled.div`
  height: 360px;
  border-radius: 12px;
  background: repeating-linear-gradient( 45deg, #f3f4f6, #f3f4f6 12px, #eef2ff 12px, #eef2ff 24px );
  border: 1px dashed #c7d2fe;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #6366f1;
  font-weight: 600;
`;

const Legend = styled.div`
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
  margin-top: 10px;
`;

const LegendItem = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 6px;
  font-size: 0.85rem;
`;

const Swatch = styled.span`
  width: 16px;
  height: 10px;
  display: inline-block;
  border-radius: 2px;
  background: ${props => props.color};
`;

const RouteList = styled.div`
  display: grid;
  gap: 10px;
`;

const RouteItem = styled.div`
  border: 1px solid #e5e7eb;
  border-left: 6px solid ${props => props.color};
  background: white;
  padding: 12px;
  border-radius: 10px;
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 10px;
`;

const Hazard = styled.div`
  font-size: 0.85rem;
  color: #6b7280;
`;

const Tips = styled.ul`
  margin: 8px 0 0 18px;
  color: #374151;
`;

function CommuteSafetyVisualizer() {
  const [home, setHome] = useState('Tokyo Station');
  const [office, setOffice] = useState('Shinjuku');
  const [timeFrom, setTimeFrom] = useState('07:00');
  const [timeTo, setTimeTo] = useState('10:00');
  const [pollutionSensitivity, setPollutionSensitivity] = useState(1.0);
  const [uvSensitivity, setUvSensitivity] = useState(0.7);
  const [temperatureSensitivity, setTemperatureSensitivity] = useState(0.5);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [result, setResult] = useState(null);

  const doPlan = async () => {
    setLoading(true); setError('');
    try {
      const data = await planCommute({
        home,
        office,
        departureWindow: { from: timeFrom, to: timeTo },
        prefs: { pollutionSensitivity, uvSensitivity, temperatureSensitivity }
      });
      setResult(data);
    } catch (e) {
      setError(e.message || 'Failed to plan commute');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Card>
        <Header>
          <Title>
            <ShieldAlert size={22} /> Commute Safety Visualizer
          </Title>
          <Button onClick={doPlan} disabled={loading}>
            <Navigation size={18} /> {loading ? 'Analyzing…' : 'Analyze Route'}
          </Button>
        </Header>

        <Grid>
          <div>
            <FieldRow>
              <div>
                <Label><MapPin size={14} style={{ marginRight: 6 }} /> Home</Label>
                <Input value={home} onChange={(e) => setHome(e.target.value)} placeholder="Enter home address/city" />
              </div>
              <div>
                <Label><MapPin size={14} style={{ marginRight: 6 }} /> Office</Label>
                <Input value={office} onChange={(e) => setOffice(e.target.value)} placeholder="Enter office address/city" />
              </div>
            </FieldRow>

            <FieldRow>
              <div>
                <Label><Clock size={14} style={{ marginRight: 6 }} /> Preferred start</Label>
                <Input type="time" value={timeFrom} onChange={(e) => setTimeFrom(e.target.value)} />
              </div>
              <div>
                <Label><Clock size={14} style={{ marginRight: 6 }} /> Preferred end</Label>
                <Input type="time" value={timeTo} onChange={(e) => setTimeTo(e.target.value)} />
              </div>
            </FieldRow>

            <Card style={{ marginTop: 12 }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 12, color: '#374151', fontWeight: 600 }}>
                <Settings size={16} /> Preferences
              </div>
              <FieldRow>
                <div>
                  <Label>Pollution sensitivity ({pollutionSensitivity.toFixed(1)})</Label>
                  <Range type="range" min="0.5" max="2" step="0.1" value={pollutionSensitivity} onChange={(e) => setPollutionSensitivity(parseFloat(e.target.value))} />
                </div>
                <div>
                  <Label>UV sensitivity ({uvSensitivity.toFixed(1)})</Label>
                  <Range type="range" min="0.5" max="2" step="0.1" value={uvSensitivity} onChange={(e) => setUvSensitivity(parseFloat(e.target.value))} />
                </div>
              </FieldRow>
              <FieldRow>
                <div>
                  <Label>Temperature sensitivity ({temperatureSensitivity.toFixed(1)})</Label>
                  <Range type="range" min="0.5" max="2" step="0.1" value={temperatureSensitivity} onChange={(e) => setTemperatureSensitivity(parseFloat(e.target.value))} />
                </div>
              </FieldRow>
            </Card>
          </div>

          <div>
            <MapPlaceholder>
              {/* Mapping provider hook point: Mapbox/Leaflet/Google. Replace this with real map implementation. */}
              Interactive Map Placeholder
            </MapPlaceholder>
            <Legend>
              {riskLegend().map((l) => (
                <LegendItem key={l.label}>
                  <Swatch color={l.color} /> {l.label}
                </LegendItem>
              ))}
            </Legend>
          </div>
        </Grid>

        {error && (
          <div style={{ marginTop: 12, color: '#b91c1c', display: 'flex', alignItems: 'center', gap: 8 }}>
            <AlertTriangle size={16} /> {error}
          </div>
        )}

        {result && (
          <Card style={{ marginTop: 16 }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 12, color: '#374151', fontWeight: 600 }}>
              <Route size={16} /> Route Options
            </div>
            <RouteList>
              {result.routes.map((r) => (
                <RouteItem key={r.id} color={r.color}>
                  <div>
                    <div style={{ fontWeight: 700, color: '#111827' }}>{r.label}</div>
                    <div style={{ fontSize: '0.9rem', color: '#6b7280' }}>Risk score: <strong style={{ color: r.color }}>{r.riskScore}</strong></div>
                    {r.hazards.length > 0 && (
                      <div style={{ marginTop: 6 }}>
                        {r.hazards.map((h, i) => (
                          <Hazard key={i}>• {h.message}</Hazard>
                        ))}
                      </div>
                    )}
                  </div>
                  <div style={{ alignSelf: 'center', fontSize: '0.8rem', color: '#6b7280' }}>
                    pm2.5: {Math.round(r.summary.pm25 || 0)} | UV: {Math.round(r.summary.uv || 0)} | Temp: {Math.round(r.summary.temp || 0)}°C
                  </div>
                </RouteItem>
              ))}
            </RouteList>
            {result.recommendation && result.recommendation.advice?.length > 0 && (
              <div style={{ marginTop: 12 }}>
                <div style={{ fontWeight: 700, color: '#111827', display: 'flex', alignItems: 'center', gap: 8 }}>
                  <Bell size={16} /> Recommended Tips
                </div>
                <Tips>
                  {result.recommendation.advice.map((tip, i) => (
                    <li key={i}>{tip}</li>
                  ))}
                </Tips>
              </div>
            )}
          </Card>
        )}
      </Card>
    </Container>
  );
}

export default CommuteSafetyVisualizer;
