  padding: 10px 20px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;

  ${props => {
    if (props.variant === 'approve') {
      return `
        background: #10b981;
        color: white;
        &:hover {
          background: #059669;
          transform: translateY(-1px);
        }
      `;
    } else if (props.variant === 'reject') {
      return `
        background: #ef4444;
