import React, { createContext, useContext, useState, useEffect } from 'react';
import toast from 'react-hot-toast';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const AuthContext = createContext();

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for existing session on app load
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      try {
        setUser(JSON.parse(savedUser));
      } catch (error) {
        localStorage.removeItem('user');
      }
    }
    setLoading(false);
  }, []);

  const login = async (email, password, role) => {
    try {
      // Simulate API call - in real app, this would be an actual API
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Mock authentication - in real app, validate credentials
      const mockUser = {
        id: Date.now(),
        email,
        role,
        name: role === 'traveler' ? 'Traveler User' : role === 'admin' ? 'Admin User' : 'Employee User',
        avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(role === 'traveler' ? 'Traveler User' : role === 'admin' ? 'Admin User' : 'Employee User')}&background=667eea&color=fff`
      };

      setUser(mockUser);
      localStorage.setItem('user', JSON.stringify(mockUser));
      toast.success(`Welcome back, ${mockUser.name}!`);
      return mockUser;
    } catch (error) {
      toast.error('Login failed. Please try again.');
      throw error;
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
    toast.success('Logged out successfully');
  };

  const value = {
    user,
    login,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}
