// Commute Service - geocoding, synthetic route generation, risk scoring using Open-Meteo (no API key)

const GEOCODE_URL = 'https://geocoding-api.open-meteo.com/v1/search';
const WEATHER_URL = 'https://api.open-meteo.com/v1/forecast';
const AIR_URL = 'https://air-quality-api.open-meteo.com/v1/air-quality';

async function geocode(place) {
  const controller = new AbortController();
  const timeout = setTimeout(() => controller.abort(), 5000);
  const res = await fetch(`${GEOCODE_URL}?name=${encodeURIComponent(place)}&count=1&language=en`, { signal: controller.signal });
  clearTimeout(timeout);
  if (!res.ok) throw new Error(`Geocoding failed: ${res.status}`);
  const data = await res.json();
  const hit = data.results && data.results[0];
  if (!hit) throw new Error(`Location not found: ${place}`);
  return { name: hit.name, lat: hit.latitude, lon: hit.longitude, country: hit.country_code };
}

function interpolateRoute(from, to, steps = 12) {
  const points = [];
  for (let i = 0; i <= steps; i++) {
    const t = i / steps;
    points.push({
      lat: from.lat + (to.lat - from.lat) * t,
      lon: from.lon + (to.lon - from.lon) * t
    });
  }
  return points;
}

// Create multiple synthetic alternative routes (straight, slight detour north, slight detour south)
function generateAlternatives(home, office) {
  const base = interpolateRoute(home, office, 14);
  const offset = (v) => v + (Math.random() * 0.06 - 0.03);

  const north = base.map((p, i) => ({ lat: p.lat + (i % 5 === 0 ? 0.03 : 0), lon: p.lon }));
  const south = base.map((p, i) => ({ lat: p.lat - (i % 5 === 0 ? 0.03 : 0), lon: p.lon }));

  return [
    { id: 'fastest', label: 'Fastest (approx.)', points: base },
    { id: 'alt_north', label: 'Alternative (north detour)', points: north },
    { id: 'alt_south', label: 'Alternative (south detour)', points: south }
  ];
}

async function fetchAirWeather(lat, lon) {
  const weatherRes = await fetch(`${WEATHER_URL}?latitude=${lat}&longitude=${lon}&hourly=temperature_2m,uv_index&current=temperature_2m,uv_index&timezone=auto`);
  const airRes = await fetch(`${AIR_URL}?latitude=${lat}&longitude=${lon}&hourly=pm2_5,pm10,ozone,nitrogen_dioxide&timezone=auto`);
  if (!weatherRes.ok) throw new Error('Weather fetch failed');
  if (!airRes.ok) throw new Error('Air fetch failed');
  const weather = await weatherRes.json();
  const air = await airRes.json();
  return { weather, air };
}

function aqiFromPm25(pm) {
  const ranges = [
    { cLow: 0.0, cHigh: 12.0, iLow: 0, iHigh: 50 },
    { cLow: 12.1, cHigh: 35.4, iLow: 51, iHigh: 100 },
    { cLow: 35.5, cHigh: 55.4, iLow: 101, iHigh: 150 },
    { cLow: 55.5, cHigh: 150.4, iLow: 151, iHigh: 200 },
    { cLow: 150.5, cHigh: 250.4, iLow: 201, iHigh: 300 }
  ];
  for (const r of ranges) {
    if (pm >= r.cLow && pm <= r.cHigh) {
      return Math.round(((r.iHigh - r.iLow) / (r.cHigh - r.cLow)) * (pm - r.cLow) + r.iLow);
    }
  }
  return Math.min(400, Math.max(0, Math.round(pm)));
}

function riskColor(score) {
  if (score <= 25) return '#10b981';
  if (score <= 50) return '#f59e0b';
  if (score <= 75) return '#ef4444';
  return '#991b1b';
}

// Compute a risk score for a point given air/weather and user preferences
function scorePoint({ pm25 = 10, uv = 2, temp = 18 }, prefs) {
  const aqi = aqiFromPm25(pm25);
  const pollutionWeight = prefs.pollutionSensitivity || 1.0;
  const uvWeight = prefs.uvSensitivity || 0.7;
  const tempWeight = prefs.temperatureSensitivity || 0.5;

  const aqiScore = Math.min(100, aqi);
  const uvScore = Math.min(100, uv * 10);
  const tempScore = Math.min(100, Math.abs(temp - 20) * 5);

  const combined = (aqiScore * pollutionWeight + uvScore * uvWeight + tempScore * tempWeight) / (pollutionWeight + uvWeight + tempWeight);
  return Math.round(combined);
}

export async function planCommute({ home, office, departureWindow, prefs }) {
  // 1) Geocode
  const [homeGeo, officeGeo] = await Promise.all([geocode(home), geocode(office)]);

  // 2) Build alternatives (synthetic paths)
  const alternatives = generateAlternatives(homeGeo, officeGeo);

  // 3) For each route, sample a subset of points and fetch air/weather at mid-point (lightweight)
  const results = await Promise.all(alternatives.map(async (route) => {
    const mid = route.points[Math.floor(route.points.length / 2)];
    try {
      const { weather, air } = await fetchAirWeather(mid.lat, mid.lon);
      // Extract representative metrics for scoring
      const hourIdx = 0;
      const pm25 = air.hourly?.pm2_5?.[hourIdx] ?? 10;
      const uv = weather.hourly?.uv_index?.[hourIdx] ?? 2;
      const temp = weather.current?.temperature_2m ?? weather.hourly?.temperature_2m?.[hourIdx] ?? 18;
      const score = scorePoint({ pm25, uv, temp }, prefs);

      const hazards = [];
      if (aqiFromPm25(pm25) > 100) hazards.push({ type: 'air', level: 'moderate', message: 'Elevated AQI along route' });
      if (uv > 7) hazards.push({ type: 'uv', level: 'high', message: 'High UV during travel' });
      if (temp < 5) hazards.push({ type: 'cold', level: 'medium', message: 'Cold temperatures expected' });
      if (temp > 32) hazards.push({ type: 'heat', level: 'high', message: 'Heat risk during travel' });

      return { ...route, riskScore: score, color: riskColor(score), summary: { pm25, uv, temp }, hazards };
    } catch (e) {
      return { ...route, riskScore: 60, color: riskColor(60), summary: {}, hazards: [{ type: 'data', level: 'low', message: 'Using fallback risk due to API issue' }] };
    }
  }));

  // 4) Recommend safest route & best window (simple pick lowest score)
  const bestRoute = results.reduce((min, r) => (r.riskScore < min.riskScore ? r : min), results[0]);

  const advice = [];
  if (bestRoute.summary.uv > 7) advice.push('Use sunscreen and wear a hat');
  if (aqiFromPm25(bestRoute.summary.pm25) > 100) advice.push('Consider a mask if sensitive to pollution');
  if (bestRoute.summary.temp < 8) advice.push('Dress warmly, consider gloves');
  if (bestRoute.summary.temp > 30) advice.push('Hydrate and avoid peak heat hours');

  return {
    home: homeGeo,
    office: officeGeo,
    routes: results.sort((a, b) => a.riskScore - b.riskScore),
    recommendation: { routeId: bestRoute.id, advice }
  };
}

export function riskLegend() {
  return [
    { label: 'Low', color: '#10b981' },
    { label: 'Moderate', color: '#f59e0b' },
    { label: 'High', color: '#ef4444' },
    { label: 'Severe', color: '#991b1b' }
  ];
}
