  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 5px;
`;

const StatusSubtitle = styled.p`
  margin: 0;
  opacity: 0.9;
  font-size: 0.9rem;
`;

function EmployeeDashboard() {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('status');
  const [workReports, setWorkReports] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [weatherAbsenceRequests, setWeatherAbsenceRequests] = useState([]);

  useEffect(() => {
    // Load saved work reports from localStorage
    const savedReports = localStorage.getItem('workReports');
    if (savedReports) {
      setWorkReports(JSON.parse(savedReports));
    }

    // Load company notifications
    const savedNotifications = localStorage.getItem('companyNotifications');
    if (savedNotifications) {
      setNotifications(JSON.parse(savedNotifications));
    } else {
      // Initialize with some default notifications
      const defaultNotifications = [
        {
          id: 1,
          title: 'New WFH Policy Update',
          message: 'Starting next week, employees can work from home up to 3 days per week.',
          type: 'info',
          date: new Date().toISOString(),
          read: false
        },
        {
          id: 2,
          title: 'Team Meeting Reminder',
          message: 'Don\'t forget about the weekly team meeting tomorrow at 10 AM.',
          type: 'reminder',
          date: new Date(Date.now() - 86400000).toISOString(),
          read: false
        }
      ];
      setNotifications(defaultNotifications);
      localStorage.setItem('companyNotifications', JSON.stringify(defaultNotifications));
    }

    // Load weather absence requests
    const savedWeatherRequests = localStorage.getItem('weatherAbsenceRequests');
    if (savedWeatherRequests) {
      setWeatherAbsenceRequests(JSON.parse(savedWeatherRequests));
    }
  }, []);

  const handleLogout = () => {
    logout();
  };

  const addWorkReport = (report) => {
    const newReport = {
      ...report,
      id: Date.now(),
      createdAt: new Date().toISOString()
    };
    const updatedReports = [newReport, ...workReports];
    setWorkReports(updatedReports);
    localStorage.setItem('workReports', JSON.stringify(updatedReports));
  };

  const deleteWorkReport = (reportId) => {
    const updatedReports = workReports.filter(r => r.id !== reportId);
    setWorkReports(updatedReports);
    localStorage.setItem('workReports', JSON.stringify(updatedReports));
  };

  const markNotificationAsRead = (notificationId) => {
    const updatedNotifications = notifications.map(n => 
      n.id === notificationId ? { ...n, read: true } : n
    );
    setNotifications(updatedNotifications);
    localStorage.setItem('companyNotifications', JSON.stringify(updatedNotifications));
  };

  const addWeatherAbsenceRequest = (request) => {
    const updatedRequests = [request, ...weatherAbsenceRequests];
    setWeatherAbsenceRequests(updatedRequests);
    localStorage.setItem('weatherAbsenceRequests', JSON.stringify(updatedRequests));
  };

  const unreadNotifications = notifications.filter(n => !n.read).length;
  const todayReports = workReports.filter(r => {
    const reportDate = new Date(r.date);
    const today = new Date();
    return reportDate.toDateString() === today.toDateString();
  }).length;
  const pendingWeatherRequests = weatherAbsenceRequests.filter(r => r.status === 'pending').length;

  return (
    <DashboardContainer>
      <Header>
        <UserInfo>
          <Avatar src={user.avatar} alt={user.name} />
          <UserDetails>
            <h2>Welcome, {user.name}!</h2>
            <p>Ready to report your work status?</p>
          </UserDetails>
        </UserInfo>
        <LogoutButton onClick={handleLogout}>
          <LogOut size={20} />
          Logout
        </LogoutButton>
      </Header>

      <TabContainer>
        <TabHeader>
          <Tab 
            active={activeTab === 'status'} 
            onClick={() => setActiveTab('status')}
          >
            <Briefcase size={20} />
            Work Status
          </Tab>
          <Tab 
            active={activeTab === 'history'} 
            onClick={() => setActiveTab('history')}
          >
            <Clock size={20} />
            History
          </Tab>
          <Tab 
            active={activeTab === 'weather'} 
            onClick={() => setActiveTab('weather')}
          >
            <CloudRain size={20} />
            Weather Absence
            {pendingWeatherRequests > 0 && (
              <span style={{
                background: '#ff6b6b',
                color: 'white',
                borderRadius: '50%',
                width: '20px',
                height: '20px',
                fontSize: '0.8rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginLeft: '5px'
              }}>
                {pendingWeatherRequests}
              </span>
            )}
          </Tab>
          <Tab 
            active={activeTab === 'notifications'} 
            onClick={() => setActiveTab('notifications')}
          >
            <Bell size={20} />
            Notifications
            {unreadNotifications > 0 && (
              <span style={{
                background: '#ff6b6b',
                color: 'white',
                borderRadius: '50%',
                width: '20px',
                height: '20px',
                fontSize: '0.8rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginLeft: '5px'
              }}>
                {unreadNotifications}
              </span>
            )}
          </Tab>
        </TabHeader>

        <TabContent>
          {activeTab === 'status' && (
            <ContentGrid>
              <Card>
                <CardTitle>
                  <FileText size={24} />
                  Report Work Status
                </CardTitle>
                <WorkStatusForm onSubmit={addWorkReport} />
              </Card>

              <div>
                <StatusCard>
                  <StatusTitle>Today's Reports</StatusTitle>
                  <StatusValue>{todayReports}</StatusValue>
                  <StatusSubtitle>Work status reports submitted today</StatusSubtitle>
                </StatusCard>

                <StatusCard>
                  <StatusTitle>Total Reports</StatusTitle>
                  <StatusValue>{workReports.length}</StatusValue>
                  <StatusSubtitle>All-time work status reports</StatusSubtitle>
                </StatusCard>
              </div>
            </ContentGrid>
          )}

          {activeTab === 'history' && (
            <Card>
              <CardTitle>
                <Clock size={24} />
                Work History
              </CardTitle>
              <WorkHistory 
                reports={workReports}
                onDelete={deleteWorkReport}
              />
            </Card>
          )}

          {activeTab === 'weather' && (
            <ContentGrid>
              <Card>
                <CardTitle>
                  <CloudRain size={24} />
                  Report Weather Absence
                </CardTitle>
                <WeatherAbsenceForm onSubmit={addWeatherAbsenceRequest} />
              </Card>

              <div>
                <StatusCard>
                  <StatusTitle>Pending Requests</StatusTitle>
                  <StatusValue>{pendingWeatherRequests}</StatusValue>
                  <StatusSubtitle>Weather absence requests awaiting approval</StatusSubtitle>
                </StatusCard>

                <StatusCard>
                  <StatusTitle>Total Requests</StatusTitle>
                  <StatusValue>{weatherAbsenceRequests.length}</StatusValue>
                  <StatusSubtitle>All-time weather absence reports</StatusSubtitle>
                </StatusCard>
              </div>
            </ContentGrid>
          )}

          {activeTab === 'weather' && (
            <Card>
              <CardTitle>
                <CloudRain size={24} />
                Weather Absence Requests
              </CardTitle>
              <AbsenceRequests 
                requests={weatherAbsenceRequests}
              />
            </Card>
          )}

          {activeTab === 'notifications' && (
            <Card>
              <CardTitle>
                <Bell size={24} />
                Company Notifications
              </CardTitle>
              <CompanyNotifications 
                notifications={notifications}
                onMarkAsRead={markNotificationAsRead}
              />
            </Card>
          )}
        </TabContent>
      </TabContainer>
    </DashboardContainer>
  );
}

export default EmployeeDashboard;
