import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useAuth } from '../../contexts/AuthContext';
import { LogOut, Plane, MapPin, Camera, AlertCircle, Sun, Image, CheckSquare, DollarSign, Phone, Calendar, BarChart3 } from 'lucide-react';
import CitySelector from './CitySelector';
import TravelAdvice from './TravelAdvice';
import WeatherAlerts from './WeatherAlerts';
import MemoryForm from './MemoryForm';
import MemoryGrid from './MemoryGrid';
import Gallery from './Gallery';
import TravelChecklist from './TravelChecklist';
import CurrencyConverter from './CurrencyConverter';
import EmergencyInfo from './EmergencyInfo';
import TripPlanner from './TripPlanner';
import WeatherGraph from './WeatherGraph';
import { getWeatherData } from '../../services/weatherService';
import { getCountryInfo } from '../../services/countryService';
import { memoryAPI } from '../../services/api';

const DashboardContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const Header = styled.header`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 20px 30px;
  margin-bottom: 30px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
`;

const Avatar = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  border: 3px solid #667eea;
`;

const UserDetails = styled.div`
  h2 {
    color: #333;
    margin: 0;
    font-size: 1.5rem;
    font-weight: 600;
  }
  p {
    color: #666;
    margin: 0;
    font-size: 0.9rem;
  }
`;

const LogoutButton = styled.button`
  background: linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%);
  color: white;
  border: none;
  padding: 12px 20px;
  border-radius: 12px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(255, 107, 107, 0.3);
  }
`;

