function TravelerDashboard() {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('explore');
  const [selectedCity, setSelectedCity] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const [countryInfo, setCountryInfo] = useState(null);
  const [memories, setMemories] = useState([]);

  useEffect(() => {
    // Load memories from backend API
    const loadMemories = async () => {
      try {
        const memoriesData = await memoryAPI.getAll();
        console.log('✅ Loaded', memoriesData.length, 'memories from backend');

        // Map backend _id to id for compatibility
        const memoriesWithId = memoriesData.map(m => ({ ...m, id: m._id }));
        setMemories(memoriesWithId);
      } catch (error) {
        console.error('❌ Error loading memories:', error.message);
        // Don't show alert on initial load, just log it
        setMemories([]);
      }
    };

    loadMemories();
  }, []);

  useEffect(() => {
    if (selectedCity) {
      // Fetch weather data and country info
      const fetchData = async () => {
        try {
          const [weather, country] = await Promise.all([
            getWeatherData(selectedCity.name),
            getCountryInfo(selectedCity.country)
          ]);
          setWeatherData(weather);
          setCountryInfo(country);
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };

      fetchData();
    }
  }, [selectedCity]);

  const handleLogout = () => {
    logout();
  };

  const addMemory = async (memory) => {
    try {
      // Memory is already saved in backend by MemoryForm
      // Reload all memories from backend to ensure consistency
      const memoriesData = await memoryAPI.getAll();
      const memoriesWithId = memoriesData.map(m => ({ ...m, id: m._id }));
      setMemories(memoriesWithId);
      console.log('✅ Memory added. Total:', memoriesWithId.length);
    } catch (error) {
      console.error('❌ Error adding memory:', error.message);
    }
  };

  const deleteMemory = async (memoryId) => {
    try {
      await memoryAPI.delete(memoryId);

      // Reload all memories from backend to ensure consistency
      const memoriesData = await memoryAPI.getAll();
      const memoriesWithId = memoriesData.map(m => ({ ...m, id: m._id }));
      setMemories(memoriesWithId);
      console.log('✅ Memory deleted. Total:', memoriesWithId.length);
    } catch (error) {
      console.error('❌ Error deleting memory:', error.message);
    }
  };

  return (
    <DashboardContainer>
      <Header>
        <UserInfo>
          <Avatar src={user.avatar} alt={user.name} />
          <UserDetails>
            <h2>Welcome, {user.name}!</h2>
            <p>Ready for your next adventure?</p>
          </UserDetails>
        </UserInfo>
        <LogoutButton onClick={handleLogout}>
          <LogOut size={20} />
          Logout
        </LogoutButton>
      </Header>

      <TabContainer>
        <TabHeader>
          <Tab 
            active={activeTab === 'explore'} 
            onClick={() => setActiveTab('explore')}
          >
            <MapPin size={20} />
            Explore
          </Tab>
          <Tab 
            active={activeTab === 'memories'} 
            onClick={() => setActiveTab('memories')}
          >
            <Camera size={20} />
            Memories
          </Tab>
          <Tab 
            active={activeTab === 'gallery'} 
            onClick={() => setActiveTab('gallery')}
          >
            <Image size={20} />
            Gallery
          </Tab>
          <Tab 
            active={activeTab === 'weather'} 
            onClick={() => setActiveTab('weather')}
          >
            <Sun size={20} />
            Weather
          </Tab>
          <Tab 
            active={activeTab === 'trip-planner'} 
            onClick={() => setActiveTab('trip-planner')}
          >
            <Calendar size={20} />
            Trip Planner
          </Tab>
          <Tab 
            active={activeTab === 'weather-graph'} 
            onClick={() => setActiveTab('weather-graph')}
          >
            <BarChart3 size={20} />
            Weather Graph
          </Tab>
        </TabHeader>

        <TabContent>
          {activeTab === 'explore' && (
            <>
              <ContentGrid columns="1fr 1fr 1fr">
                <Card>
                  <CardTitle>
                    <MapPin size={24} />
                    Select Destination
                  </CardTitle>
                  <CitySelector 
                    onCitySelect={setSelectedCity}
                    selectedCity={selectedCity}
                  />
                </Card>

                <Card>
                  <CardTitle>
                    <DollarSign size={24} />
                    Currency Converter
                  </CardTitle>
                  <CurrencyConverter />
                </Card>

                <Card>
                  <CardTitle>
                    <Phone size={24} />
                    Emergency Info
                  </CardTitle>
                  <EmergencyInfo />
                </Card>
              </ContentGrid>

              {selectedCity && (
                <Card style={{ marginTop: '30px' }}>
                  <CardTitle>
                    <Plane size={24} />
                    Travel Advice for {selectedCity.name}
                  </CardTitle>
                  <TravelAdvice 
                    city={selectedCity}
                    countryInfo={countryInfo}
                  />
                </Card>
              )}

              <ContentGrid columns="1fr">
                <Card style={{ marginTop: selectedCity ? '30px' : '0' }}>
                  <CardTitle>
                    <CheckSquare size={24} />
                    Travel Checklist
                  </CardTitle>
                  <TravelChecklist />
                </Card>
              </ContentGrid>
            </>
          )}

          {activeTab === 'memories' && (
            <ContentGrid>
              <Card>
                <CardTitle>
                  <Camera size={24} />
                  Add Memory
                </CardTitle>
                <MemoryForm onSubmit={addMemory} />
              </Card>

              <Card>
                <CardTitle>
                  <Camera size={24} />
                  Your Memories
                </CardTitle>
                <MemoryGrid 
                  memories={memories}
                  onDelete={deleteMemory}
                />
              </Card>
            </ContentGrid>
          )}

          {activeTab === 'gallery' && (
            <Gallery memories={memories} />
          )}

          {activeTab === 'weather' && (
            <Card>
              <CardTitle>
                <AlertCircle size={24} />
                Weather Alerts
              </CardTitle>
              <WeatherAlerts 
                selectedCity={selectedCity}
                weatherData={weatherData}
              />
            </Card>
          )}

          {activeTab === 'trip-planner' && (
            <TripPlanner />
          )}

          {activeTab === 'weather-graph' && (
            <WeatherGraph />
          )}
        </TabContent>
      </TabContainer>
    </DashboardContainer>
  );
}

export default TravelerDashboard;
