import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { 
  Home, Menu, Search, Heart, MoreVertical,
  ChevronLeft, ChevronRight,
  Facebook, Twitter, Linkedin
} from 'lucide-react';

// Main Container
const PageContainer = styled.div`
  min-height: 100vh;
  width: 100%;
  background: #ff69b4; /* Bright pink background */
  padding: 0;
  margin: 0;
  position: relative;
`;

// Top Navigation Bar (Light Blue)
const TopNav = styled.nav`
  background: #87ceeb; /* Light blue */
  padding: 12px 30px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 0.85rem;
  font-weight: 600;
  text-transform: uppercase;
`;

const TopNavLeft = styled.div`
  display: flex;
  gap: 25px;
`;

const TopNavLink = styled.a`
  color: white;
  text-decoration: none;
  cursor: pointer;
  
  &:hover {
    opacity: 0.8;
  }
`;

const TopNavRight = styled.div`
  display: flex;
  gap: 15px;
  align-items: center;
`;

const SocialIcon = styled.a`
  color: white;
  cursor: pointer;
  
  &:hover {
    opacity: 0.8;
  }
`;

// Logo and Banner Area (White Background)
const HeaderSection = styled.header`
  background: white;
  padding: 20px 30px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.div`
  font-size: 3rem;
  font-weight: 900;
  color: #dc143c; /* Red */
  letter-spacing: 2px;
`;

const BannerAd = styled.div`
  background: #2c2c2c;
  color: white;
  padding: 15px 25px;
  border-radius: 4px;
  display: flex;
  align-items: center;
  gap: 15px;
  max-width: 350px;
`;

const BannerLogo = styled.span`
  color: #ff8c00; /* Orange */
  font-weight: 700;
  font-size: 1.1rem;
`;

const BannerText = styled.span`
  font-size: 0.85rem;
`;

// Main Navigation Bar (Dark Blue)
const MainNav = styled.nav`
  background: #1e3a5f; /* Dark blue */
  padding: 15px 30px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const MainNavLeft = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
`;

const NavIconButton = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  padding: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  
  &:hover {
    opacity: 0.8;
  }
`;

const MainNavLinks = styled.div`
  display: flex;
  gap: 25px;
`;

const MainNavLink = styled.a`
  color: white;
  text-decoration: none;
  font-weight: 600;
  text-transform: uppercase;
  font-size: 0.9rem;
  
  &:hover {
    opacity: 0.8;
  }
`;

const MainNavRight = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
`;

const FollowButton = styled.button`
  background: #dc143c; /* Red */
  color: white;
  border: none;
  padding: 10px 20px;
  font-weight: 700;
  text-transform: uppercase;
  cursor: pointer;
  font-size: 0.9rem;
  
  &:hover {
    background: #b0122a;
  }
`;

const LoginButton = styled(Link)`
  background: #28a745; /* Green for login */
  color: white;
  border: none;
  padding: 10px 20px;
  font-weight: 700;
  text-transform: uppercase;
  cursor: pointer;
  font-size: 0.9rem;
  text-decoration: none;
  display: inline-block;
  
  &:hover {
    background: #218838;
  }
`;

const SearchIconButton = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  padding: 8px;
  
  &:hover {
    opacity: 0.8;
  }
`;

// Content Area
const ContentArea = styled.div`
  background: white;
  margin: 0 auto;
  max-width: 1400px;
  padding: 40px 30px;
`;

// Latest Updates Section
const LatestUpdatesSection = styled.div`
  margin-bottom: 40px;
`;

const LatestUpdatesHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 20px;
`;

const LatestUpdatesButton = styled.button`
  background: #ff69b4; /* Pink */
  color: white;
  border: none;
  padding: 12px 25px;
  font-weight: 700;
  text-transform: uppercase;
  font-size: 0.9rem;
  cursor: pointer;
  white-space: nowrap;
  
  &:hover {
    background: #ff1493;
  }
`;

const ScrollableSection = styled.div`
  display: flex;
  gap: 20px;
  overflow-x: auto;
  padding: 10px 0;
  position: relative;
  
  &::-webkit-scrollbar {
    display: none;
  }
  -ms-overflow-style: none;
  scrollbar-width: none;
`;

const ScrollButton = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: #4a90e2; /* Blue */
  color: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  z-index: 10;
  
  &:hover {
    background: #357abd;
  }
  
  &:first-of-type {
    left: 10px;
  }
  
  &:last-of-type {
    right: 10px;
  }
`;

const PreviewCard = styled.div`
  min-width: 280px;
  background: white;
  border: 1px solid #e0e0e0;
  border-radius: 4px;
  overflow: hidden;
  cursor: pointer;
  
  &:hover {
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  }
`;

const PreviewCardImage = styled.div`
  width: 100%;
  height: 180px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 3rem;
`;

const PreviewCardContent = styled.div`
  padding: 15px;
`;

const PreviewCardTitle = styled.h4`
  font-size: 0.95rem;
  color: #333;
  margin: 0;
  font-weight: 600;
  line-height: 1.4;
`;

// Article Grid
const ArticleGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 25px;
  
  @media (max-width: 1200px) {
    grid-template-columns: repeat(2, 1fr);
  }
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const ArticleCard = styled.div`
  background: white;
  border: 1px solid #e0e0e0;
  border-radius: 4px;
  overflow: hidden;
  position: relative;
  cursor: pointer;
  
  &:hover {
    box-shadow: 0 6px 12px rgba(0,0,0,0.15);
  }
  
  ${props => props.large && `
    grid-column: span 1;
    
    @media (min-width: 1200px) {
      grid-column: span 2;
      grid-row: span 2;
    }
  `}
`;

const ArticleImage = styled.div`
  width: 100%;
  height: ${props => props.large ? '400px' : '250px'};
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${props => props.large ? '5rem' : '3rem'};
`;

const ArticleOverlay = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  display: flex;
  gap: 10px;
  z-index: 5;
`;

const OverlayButton = styled.button`
  background: rgba(255, 255, 255, 0.9);
  border: none;
  width: 35px;
  height: 35px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #333;
  
  &:hover {
    background: white;
  }
`;

const AddButton = styled.button`
  position: absolute;
  bottom: 15px;
  right: 15px;
  background: #20b2aa; /* Teal-green */
  color: white;
  border: none;
  width: 45px;
  height: 45px;
  border-radius: 50%;
  font-size: 1.5rem;
  font-weight: 300;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 5;
  
  &:hover {
    background: #1a9b93;
  }
`;

const ArticleContent = styled.div`
  padding: 20px;
`;

const ArticleTitle = styled.h3`
  font-size: 1.1rem;
  color: #333;
  margin: 0 0 10px 0;
  font-weight: 700;
  line-height: 1.4;
`;

const ArticleMeta = styled.div`
  color: #999;
  font-size: 0.85rem;
  margin-bottom: 15px;
`;

const ArticleDescription = styled.p`
  color: #666;
  font-size: 0.9rem;
  line-height: 1.6;
  margin: 0 0 15px 0;
`;

const ReadMoreButton = styled.button`
  background: #4a90e2; /* Blue */
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-weight: 600;
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: 8px;
  
  &:hover {
    background: #357abd;
  }
`;

const ArticleStats = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 15px;
  color: #666;
  font-size: 0.9rem;
`;

const StatItem = styled.div`
  display: flex;
  align-items: center;
  gap: 6px;
`;

function HomePage() {
  const [latestScroll, setLatestScroll] = useState(0);

  const scrollLatest = (direction) => {
    const container = document.getElementById('latest-updates-scroll');
    if (container) {
      const scrollAmount = 300;
      const newScroll = direction === 'left' 
        ? latestScroll - scrollAmount 
        : latestScroll + scrollAmount;
      container.scrollTo({ left: newScroll, behavior: 'smooth' });
      setLatestScroll(newScroll);
    }
  };

  // Mock article data
  const articles = [
    {
      id: 1,
      large: true,
      image: 'üì±',
      title: "LOVE LIFE'S SWEETEST REWARD LET IT FLOW IT FLOATS BACK TO YOU",
      meta: "1 Hour ago",
      showAdd: true
    },
    {
      id: 2,
      image: 'üèîÔ∏è',
      title: "NEVER HEARD THE WORD IMPOSSIBLE THIS",
      meta: "1 Hour ago",
      showOverlay: true,
      showAdd: true
    },
    {
      id: 3,
      image: 'üèÉ',
      title: "I NEVER THOUGHT I COULD FEEL SO FREE",
      meta: "1 Hour ago",
      showOverlay: true,
      showAdd: true
    },
    {
      id: 4,
      image: null,
      title: "PLACE YOU OUGHT TO BE SO THEY LOADED UP",
      meta: "1 Hour ago",
      description: "Hills that is. Swimmin' pools movie stars. Just two good of boys Wouldn't change if they could.",
      likes: 8,
      comments: 13
    },
    {
      id: 5,
      image: 'üö¥',
      title: "THE FIRST MATE AND HIS SKIPPER TOO WILL",
      meta: "1 Hour ago",
      showOverlay: true,
      showAdd: true
    }
  ];

  const previewCards = [
    {
      id: 1,
      title: "TRAVELLED DOWN THE ROAD AND BACK AGAIN"
    },
    {
      id: 2,
      title: "YOUR HEART IS TRUE YOU'RE A PAL AND A CONFIGANT"
    },
    {
      id: 3,
      title: "MINNOW WOULD BE LOST"
    },
    {
      id: 4,
      title: "SOMEWHERE GREEN AND WIDE OPEN"
    },
    {
      id: 5,
      title: "TAKE A BREAK AND SEE WHAT'S AROUND"
    }
  ];

  return (
    <PageContainer>
      {/* Top Navigation Bar */}
      <TopNav>
        <TopNavLeft>
          <TopNavLink href="#about">ABOUT</TopNavLink>
          <TopNavLink href="#post-format">POST FORMAT</TopNavLink>
          <TopNavLink href="#sitemap">SITEMAP</TopNavLink>
          <TopNavLink href="#contact">CONTACT</TopNavLink>
        </TopNavLeft>
        <TopNavRight>
          <SocialIcon href="#" aria-label="Facebook">
            <Facebook size={18} />
          </SocialIcon>
          <SocialIcon href="#" aria-label="Twitter">
            <Twitter size={18} />
          </SocialIcon>
          <SocialIcon href="#" aria-label="Google Plus">
            <span style={{ fontSize: '18px', fontWeight: 'bold' }}>G+</span>
          </SocialIcon>
          <SocialIcon href="#" aria-label="Pinterest">
            <span style={{ fontSize: '18px', fontWeight: 'bold' }}>P</span>
          </SocialIcon>
          <SocialIcon href="#" aria-label="LinkedIn">
            <Linkedin size={18} />
          </SocialIcon>
        </TopNavRight>
      </TopNav>

      {/* Logo and Banner Area */}
      <HeaderSection>
        <Logo>GRANZIM</Logo>
        <BannerAd>
          <BannerLogo>themeforest</BannerLogo>
          <BannerText>Site Templates and Themes</BannerText>
        </BannerAd>
      </HeaderSection>

      {/* Main Navigation Bar */}
      <MainNav>
        <MainNavLeft>
          <NavIconButton aria-label="Home">
            <Home size={20} />
          </NavIconButton>
          <NavIconButton aria-label="Menu">
            <Menu size={20} />
          </NavIconButton>
          <MainNavLinks>
            <MainNavLink href="#entertainment">ENTERTAINMENT</MainNavLink>
            <MainNavLink href="#fashion">FASHION</MainNavLink>
            <MainNavLink href="#music">MUSIC</MainNavLink>
            <MainNavLink href="#sports">SPORTS</MainNavLink>
            <MainNavLink href="#people">PEOPLE</MainNavLink>
            <MainNavLink href="#travel">TRAVEL</MainNavLink>
            <MainNavLink href="#contact">CONTACT</MainNavLink>
          </MainNavLinks>
        </MainNavLeft>
        <MainNavRight>
          <FollowButton>+ FOLLOW</FollowButton>
          <LoginButton to="/login">LOGIN</LoginButton>
          <SearchIconButton aria-label="Search">
            <Search size={20} />
          </SearchIconButton>
        </MainNavRight>
      </MainNav>

      {/* Content Area */}
      <ContentArea>
        {/* Latest Updates Section */}
        <LatestUpdatesSection>
          <LatestUpdatesHeader>
            <LatestUpdatesButton>LATEST UPDATES</LatestUpdatesButton>
            <div style={{ position: 'relative', flex: 1 }}>
              <ScrollButton onClick={() => scrollLatest('left')} style={{ left: '10px' }}>
                <ChevronLeft size={20} />
              </ScrollButton>
              <ScrollableSection id="latest-updates-scroll">
                {previewCards.map((card) => (
                  <PreviewCard key={card.id}>
                    <PreviewCardImage>üì∞</PreviewCardImage>
                    <PreviewCardContent>
                      <PreviewCardTitle>{card.title}</PreviewCardTitle>
                    </PreviewCardContent>
                  </PreviewCard>
                ))}
              </ScrollableSection>
              <ScrollButton onClick={() => scrollLatest('right')} style={{ right: '10px' }}>
                <ChevronRight size={20} />
              </ScrollButton>
            </div>
          </LatestUpdatesHeader>
        </LatestUpdatesSection>

        {/* Article Grid */}
        <ArticleGrid>
          {articles.map((article) => (
            <ArticleCard key={article.id} large={article.large}>
              {article.image && (
                <ArticleImage large={article.large}>
                  {article.image}
                  {article.showOverlay && (
                    <ArticleOverlay>
                      <OverlayButton>
                        <Heart size={18} />
                      </OverlayButton>
                      <OverlayButton>
                        <MoreVertical size={18} />
                      </OverlayButton>
                    </ArticleOverlay>
                  )}
                  {article.showAdd && <AddButton>+</AddButton>}
                </ArticleImage>
              )}
              <ArticleContent>
                <ArticleTitle>{article.title}</ArticleTitle>
                <ArticleMeta>{article.meta}</ArticleMeta>
                {article.description && (
                  <>
                    <ArticleDescription>{article.description}</ArticleDescription>
                    <ReadMoreButton>
                      ‚Üí Read More
                    </ReadMoreButton>
                    <ArticleStats>
                      <StatItem>
                        <Heart size={16} /> {article.likes}
                      </StatItem>
                      <StatItem>
                        üí¨ {article.comments}
                      </StatItem>
                    </ArticleStats>
                  </>
                )}
              </ArticleContent>
            </ArticleCard>
          ))}
        </ArticleGrid>
      </ContentArea>
    </PageContainer>
  );
}

export default HomePage;
